class ChainRelayAlpha: 
    signature_threshold: int # 0 < signature_threshold <= 512
    trusting_period: int # validators are trusted for a certain time, ensuring they have not exited the validator set, in unix time
    current_validator_set: List[Bytes48]
    current_validator_set_aggregate: Bytes48
    next_validator_set: List[Bytes48]
    next_validator_set_aggregate: Bytes48
    finalized_block_root: Bytes32
    latest_slot: int
    latest_slot_with_validator_set_change: int
    current_eth1_timestamp: int # implicitly provided by eth1, unix timestamp
    finalized_slot: int
    
    def __init__(self, trusted_finalized_block_root: Bytes32, trusted_current_validator_set: List[Bytes48], trusted_current_validator_set_aggregate: Bytes48, trusted_next_validator_set: List[Bytes48], trusted_next_validator_set_aggregate: Bytes48, signature_threshold: int, trusting_period: int, latest_slot: int, finalized_slot: int, latest_slot_with_validator_set_change: int): 
        self.finalized_block_root = trusted_finalized_block_root
        self.current_validator_set = trusted_current_validator_set
        self.current_validator_set_aggregate = trusted_current_validator_set_aggregate
        self.next_validator_set = trusted_next_validator_set
        self.next_validator_set_aggregate = trusted_next_validator_set_aggregate
        self.signature_threshold = signature_threshold
        self.trusting_period = trusting_period
        self.latest_slot = latest_slot
        self.latest_slot_with_validator_set_change = latest_slot_with_validator_set_change
        self.finalized_slot = finalized_slot
    
    def submit_update(self, signature: Bytes96, 
                            participants: List[bool], 
                            latest_block_root: Bytes32,
                            signing_domain: Bytes32,  
                            slot: int, 
                            slot_branch: List[Bytes32], 
                            finalized_block_root: Bytes32, 
                            finalizing_branch: List[Bytes32], 
                            finalized_slot: int, 
                            finalized_slot_branch: List[Bytes32],
                            state_root: Bytes32,
                            state_branch: List[Bytes32],
                            nextnext_validator_set: List[Bytes48] = [], 
                            nextnext_validator_set_aggregate: Bytes48 = None, 
                            validator_set_branch: List[Bytes32] = []): 
        signing_root = bytes(compute_signing_root(latest_block_root, signing_domain))
        # check if trusting period has elapsed
        if self.current_eth1_timestamp > self.trusting_period + slot_to_unix_timestamp(self.latest_slot_with_validator_set_change): 
            raise Exception('trusting period since validators were updated elapsed')
        # check if enough signatures
        if sum(participants) < self.signature_threshold: 
            raise Exception('not enough participants in aggregate signature')
        # check if state proof is valid
        if not validate_merkle_branch(latest_block_root, state_root, STATE_ROOT_INDEX, state_branch): 
            raise Exception('state proof invalid')
        # check if finality proof is valid
        if not validate_merkle_branch(state_root, finalized_block_root, FINALIZED_ROOT_INDEX, finalizing_branch): 
            raise Exception('finality proof invalid')
        if not validate_merkle_branch(latest_block_root, merkleize_slot(slot), SLOT_INDEX, slot_branch): 
            # raise Exception('slot proof invalid')
            print("slot proof invalid for slot", slot)
        if not validate_merkle_branch(finalized_block_root, merkleize_slot(finalized_slot), SLOT_INDEX, finalized_slot_branch): 
            # raise Exception('finalized slot proof invalid')
            print("finalized slot proof invalid for slot", slot)
        print("slot difference is ", slot - self.latest_slot)
        # check if current epoch
        if (slot - self.latest_slot) // (SLOTS_PER_EPOCH * EPOCHS_PER_SYNC_COMMITTEE_PERIOD) == 0: 
            # check if signature is valid
            if not validate_aggregate_sig_32nature(signing_root, participants, signature, self.current_validator_set, self.current_validator_set_aggregate): 
                raise Exception('signature of current sync committee not valid')
        # check if next epoch
        elif (slot - self.latest_slot) // (SLOTS_PER_EPOCH * EPOCHS_PER_SYNC_COMMITTEE_PERIOD) == 1:  
            print("checking in next sync committee")
            # check if signature is valid
            if not validate_aggregate_sig_32nature(signing_root, participants, signature, self.next_validator_set, self.next_validator_set_aggregate): 
                raise Exception('signature of next sync committee not valid')
            # check if validators proof is valid
            if not validate_merkle_branch(state_root, hash_tree_root_sync_committee(nextnext_validator_set, nextnext_validator_set_aggregate), NEXT_SYNC_COMMITTEE_INDEX, validator_set_branch): 
                # raise Exception('validator update proof invalid')
                None
            # apply validators update
            self.current_validator_set = self.next_validator_set
            self.current_validator_set_aggregate = self.next_validator_set_aggregate
            self.next_validator_set = nextnext_validator_set
            self.next_validator_set_aggregate = nextnext_validator_set_aggregate
            self.latest_slot_with_validator_set_change = finalized_slot
        else: 
            raise Exception('update not from current or next epoch')
        # apply root update
        self.finalized_block_root = finalized_block_root
        self.latest_slot = slot
        self.finalized_slot = finalized_slot

    def get_current_header(self): 
        return self.finalized_block_root

# BLS SIGNATURES
def bls_FastAggregateVerify(participants_public_keys_32: List[Bytes48], signed_data: Bytes32, signature: Bytes96):
    # BLS_SIG_BLS12381G2_XMD:SHA-256_SSWU_RO_POP_ FastAggregateVerify PRECOMPILE
    return bls_pop.FastAggregateVerify(participants_public_keys_32, bytes(signed_data), bytes(signature))

def slot_to_unix_timestamp(slot: uint64): 
    # return slot * 12s + first eth2 timestamp
    return int(slot) * 12 + 1606824023 # 1606824023 = first Eth2 block at Dec-01-2020 12:00:23 PM +UTC

def compute_signing_root(block_root: Bytes32, domain: Bytes32): 
    return hash_tree_root_pair(block_root, domain)

def validate_aggregate_sig_32nature(signed_root: Bytes32, participants: List[bool], signature: Bytes96, trusted_validator_set: List[Bytes48], trusted_validator_set_aggregate: Bytes48): 
    # return true if valid, false if not
    participants_public_keys_32 = [bytes(validator) for (participating_bool, validator) in zip(participants, trusted_validator_set) if participating_bool]
    print("validator set ", [PK.hex() for PK in participants_public_keys_32])
    print("validating signature ", signature.hex())
    print("validating message", signed_root.hex())
    return bls_FastAggregateVerify(participants_public_keys_32, bytes(signed_root), bytes(signature))

def validate_merkle_branch(root: Bytes32, leaf: Bytes32, generalized_index: int, branch: List[Bytes32]):
    # return true if valid, false if not
    # from eth2.0-spec
    index = get_subtree_index(generalized_index)
    depth = floorlog2(generalized_index)
    value = leaf
    for i in range(depth):
        if index // (2**i) % 2:
            value = hash(branch[i] + value)
        else:
            value = hash(value + branch[i])
    return value == root


def merkleize_slot(slot: int) -> bytes: 
    slot = slot.to_bytes(8, "little")
    slot += b'\0' * 24
    return slot

# from eth2.0-spec
def floorlog2(x: int) -> uint64:
    if x < 1:
        raise ValueError(f"floorlog2 accepts only positive values, x={x}")
    return uint64(x.bit_length() - 1)

def ceillog2(x: int) -> uint64:
    if x < 1:
        raise ValueError(f"ceillog2 accepts only positive values, x={x}")
    return uint64((x-1).bit_length())

# from eth2.0-spec
def get_subtree_index(generalized_index: int) -> uint64:
    return uint64(generalized_index % 2**(floorlog2(generalized_index)))

def next_pow_of_two(x): 
    return 1 if x == 0 else 2**(x - 1).bit_length()

def hash(x: Union[bytes, bytearray]) -> Bytes32:
    return Bytes32(sha256(bytes(x)).digest())

#BLSPubkey = list[byte] # BLSPubkey is an array of 48 bytes
#chunk = list[byte]
    #byte[32]

def hash_tree_root_pair(left: bytearray, right: bytearray) -> bytearray: 
    combined = left + right
    return hash(combined)

# hash-tree-root of a list of 32-bytes
# takes a list of 32-bytes-lists, returns a 32-byte-list
def merkleize (chunks: list[bytearray]) -> bytearray:
    # if chunks.__len__() == 2: 
    #   return hash_tree_root(chunks[0], chunks[1]) 
    length_next_pow = next_pow_of_two(chunks.__len__())
    # print("[DEBUG] Chunk length is " + str(chunks.__len__()))
    # print("[DEBUG] Next Pow is " + str(length_next_pow))
    for i in range(chunks.__len__(), length_next_pow):
        #print("[DEBUG] Filling tree with zero chunks at " + str(i))
        chunks.insert(i, bytearray(32)) # fill lowest level of tree
    for level in range(0, floorlog2(length_next_pow)):
        #print("[DEBUG] At level", level)
        step = pow(2,level)
        for current_accumulator in range(0, length_next_pow, step*2):
            #print("[DEBUG] Current element index", current_accumulator)
            chunks[current_accumulator] = hash_tree_root_pair(chunks[current_accumulator], chunks[current_accumulator+step])
    return chunks[0] # the merkleize as binary tree function should be here, this is just a placeholder

def hash_tree_root_blspubkey (blspubkey: bytearray) -> bytearray:
    (blspubkey_merkleized_left, blspubkey_merkleized_right)  = (blspubkey[0:32], blspubkey[32:48] + bytearray(b'\x00')*16)
    return hash_tree_root_pair(blspubkey_merkleized_left, blspubkey_merkleized_right)

# determine hash_tree_root for a Sync Committee (list of BLSPubkeys)
# takes a list of 48-byte lists and returns a 32-byte list
def hash_tree_root_sync_committee (sync_committee: list[bytearray], sync_aggregate: bytearray) -> bytearray:
    sync_committee_hashed = [hash_tree_root_blspubkey(blspubkey) for blspubkey in sync_committee]
    sync_committee_hashed_merkleized = merkleize(sync_committee_hashed) # theoretically there is a limit = N here, but that doesn't affect the function in this case
    # root = hash_tree_root_pair(sync_committee_hashed_merkleized, len(sync_committee).to_bytes(32, byteorder="little")) # mix in length
    root = hash_tree_root_pair(sync_committee_hashed_merkleized,hash_tree_root_blspubkey(sync_aggregate))
    return root

