def bytesToSol(inputArray):
    string = "["
    for elem in ['bytes(hex"' + inputArrayElem.hex() + '")' for inputArrayElem in inputArray]:
        string = string + elem + "," 
    string = string[:-1] + "]"
    return string

def proofToSol(inputArray):
    string = "["
    for elem in ['bytes32(hex"' + inputArrayElem.hex() + '")' for inputArrayElem in inputArray]:
        string = string + elem + "," 
    string = string[:-1] + "]"
    return string

def blsToSol(inputArray):
    string = "["
    for elem in ['bytes(hex"' + inputArrayElem + '")' for inputArrayElem in inputArray]:
        string = string + elem + "," 
    string = string[:-1] + "]"
    return string

def giveMeHex(input): 
    return ['0x' + elem.hex() for elem in input]

def giveMeHexFromVals(input): 
    return ['0x' + str(elem) for elem in input]


